echo Twittifyjavac -cp "json.jar" *.javaif [ "$(uname)" == "Darwin" ] ; then java -cp ".:json.jar" twittify elif [ "$(expr substr $(uname -s) 1 5)" == "Linux" ] ; then java -cp ".:json.jar" twittify elif [ "$(xpr substr $(uname -s) 1 10)" == "MINGW32_NT" ] ; then java -cp ".;json.jar" twittify elif [ "$(expr substr $(uname -s) 1 10)" == "MINGW64_NT"]; then java -cp ".;json.jar" twittify fi